import React, { useEffect, useState } from "react";
import axios from "axios";

// Define Todo type
interface Todo {
  id: number;
  task: string;
  completed: boolean;
}

const App: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [task, setTask] = useState<string>("");
  const [searchFilter, setSearchFilter] = useState<string>("all");

  const API_BASE = "http://localhost:8080/api/todos";

  // Fetch Todos
  const fetchTodos = async () => {
    try {
      const response = await axios.get<Todo[]>(
        searchFilter === "completed"
          ? `${API_BASE}/completed`
          : searchFilter === "incomplete"
          ? `${API_BASE}/incomplete`
          : `${API_BASE}/`
      );
      setTodos(response.data);
    } catch (error) {
      console.error("Error fetching todos", error);
    }
  };

  useEffect(() => {
    fetchTodos();
  }, [searchFilter]);

  // Add Todo
  const addTodo = async () => {
    if (!task.trim()) {
      alert("Task is required");
      return;
    }

    try {
      const response = await axios.post<Todo>(`${API_BASE}/`, {
        task: task.trim(),
        completed: false,
      });

      // Update the state with the new Todo
      setTodos((prev) => [...prev, response.data]);

      // Clear the input field
      setTask("");
    } catch (error) {
      console.error("Error adding todo:", error);
      alert("Failed to add task. Please try again.");
    }
  };

  // Update Todo
  const updateTodo = async (id: number, updatedTodo: Partial<Todo>) => {
    try {
      const response = await axios.put<Todo>(`${API_BASE}/${id}`, {
        ...updatedTodo,
        id,
      });
      setTodos((prev) =>
        prev.map((todo) => (todo.id === id ? response.data : todo))
      );
    } catch (error) {
      console.error("Error updating todo", error);
    }
  };

  // Delete Todo
  const deleteTodo = async (id: number) => {
    try {
      await axios.delete(`${API_BASE}/${id}`);
      setTodos((prev) => prev.filter((todo) => todo.id !== id));
    } catch (error) {
      console.error("Error deleting todo", error);
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>Todo App</h1>

      {/* Add Todo Form */}
      <div style={{ marginBottom: "20px" }}>
        <h2>Add Todo</h2>
        <input
          type="text"
          placeholder="Task"
          value={task}
          onChange={(e) => setTask(e.target.value)}
        />
        <button onClick={addTodo}>Add</button>
      </div>

      {/* Filter Todos */}
      <div style={{ marginBottom: "20px" }}>
        <h2>Filter Todos</h2>
        <select
          value={searchFilter}
          onChange={(e) => setSearchFilter(e.target.value)}
        >
          <option value="all">All</option>
          <option value="completed">Completed</option>
          <option value="incomplete">Incomplete</option>
        </select>
      </div>

      {/* Todo List */}
      <div>
        <h2>Todo List</h2>
        {todos.map((todo) => (
          <div
            key={todo.id}
            style={{
              border: "1px solid #ccc",
              padding: "10px",
              marginBottom: "10px",
            }}
          >
            <h3>
              {todo.task}{" "}
              {todo.completed && (
                <span style={{ color: "green" }}>(Completed)</span>
              )}
            </h3>
            <button
              onClick={() =>
                updateTodo(todo.id, { completed: !todo.completed })
              }
            >
              {todo.completed ? "Mark Incomplete" : "Mark Complete"}
            </button>
            <button onClick={() => deleteTodo(todo.id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
